mapper.update:
update article
article_title = #{articleTitle}, article_summary = #{articleSummary}, article_content = #{articleContent}, article_view_count = #{articleViewCount}, article_like_count = #{articleLikeCount}, article_comment_count = #{articleCommentCount}, created_time = #{createdTime}, update_time = #{updateTime}, is_deleted = #{isDeleted}, article_category_name = #{articleCategoryName}
where article_id = #{articleId}

mapper.delete:
update article
set is_deleted = 1
where article_id = #{articleId}

mapper.select:
select *
from article,
     user
where article_user_id = user_id
  and article_id = #{articleId}
  and is_deleted = 0
